import React, { useState } from "react";
import {
  Form,
  Button,
  Container,
  Row,
  Col,
  Card,
  Alert,
} from "react-bootstrap";
import "./UstawieniaKonta.css";
import guitarUser1 from "../assets/guitarist1.jpg";

const UstawieniaKonta = () => {
  const [user, setUser] = useState({
    Nazwa: "Krzysztof Urbański",
    email: "krzysztofurban@gmail.com",
    numer: "606-942-106",
    opis: "Cześć!\nJestem Krzysztof i uwielbiam grać na gitarze. Szukam zespołu do wspólnego muzykowania. Gram różne style, od klasycznego rocka po bluesa i jazz. Chcę znaleźć inspirujących ludzi, którzy mają ochotę na wspólne granie i tworzenie czegoś fajnego. Jeśli szukacie gitarzysty to dobrze trafiliście!",
    miasto: "Wrocław",
    zdjecie: guitarUser1,
    haslo: "Alamakota1!",
    widocznosc: true,
  });

  const [showAlert, setShowAlert] = useState(false);

  const handleSaveChanges = () => {
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 3000);
  };

  const handleFileChange = (e) => {
    setUser({ ...user, zdjecie: URL.createObjectURL(e.target.files[0]) });
  };

  const handleVisibilityChange = () => {
    setUser({ ...user, widocznosc: !user.widocznosc });
  };

  return (
    <Container fluid className="pageBackground">
      <Row className="mt-5">
        <Col md={{ span: 6, offset: 3 }}>
          <Card>
            <Card.Body>
              <Card.Title>Twoje konto</Card.Title>
              <Form>
                <Form.Group className="formGroup">
                  <Form.Label>Imię i Nazwisko</Form.Label>
                  <Form.Control
                    type="text"
                    name="Nazwa"
                    value={user.Nazwa}
                    onChange={(e) =>
                      setUser({ ...user, Nazwa: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Label>E-mail</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={user.email}
                    onChange={(e) =>
                      setUser({ ...user, email: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Label>Hasło</Form.Label>
                  <Form.Control
                    type="password"
                    name="haslo"
                    value={user.haslo}
                    onChange={(e) =>
                      setUser({ ...user, haslo: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Label>Numer kontaktowy</Form.Label>
                  <Form.Control
                    type="text"
                    name="numer"
                    value={user.numer}
                    onChange={(e) =>
                      setUser({ ...user, numer: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Label>Miasto</Form.Label>
                  <Form.Control
                    as="select"
                    name="miasto"
                    value={user.miasto}
                    onChange={(e) =>
                      setUser({ ...user, miasto: e.target.value })
                    }
                  >
                    <option value="Warszawa">Warszawa</option>
                    <option value="Kraków">Kraków</option>
                    <option value="Gdańsk">Gdańsk</option>
                    <option value="Wrocław">Wrocław</option>
                    <option value="Poznań">Poznań</option>
                    <option value="Rzeszów">Rzeszów</option>
                  </Form.Control>
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Label>Opis</Form.Label>
                  <Form.Control
                    as="textarea"
                    name="opis"
                    value={user.opis}
                    onChange={(e) => setUser({ ...user, opis: e.target.value })}
                  />
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Check
                    type="checkbox"
                    id="widocznoscCheckbox"
                    label="Czy chcesz, aby twój profil był widoczny?"
                    checked={user.widocznosc}
                    onChange={handleVisibilityChange}
                  />
                </Form.Group>
                <Form.Group className="formGroup">
                  <Form.Label>Zdjęcie</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handleFileChange}
                  />
                  {user.zdjecie && (
                    <img
                      id="userImage"
                      src={user.zdjecie}
                      alt="Podgląd zdjęcia"
                    />
                  )}
                </Form.Group>
                <Button
                  variant="primary"
                  className="w-100"
                  id="saveBtn"
                  onClick={handleSaveChanges}
                >
                  Zapisz zmiany
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      {showAlert && (
        <Row className="mt-3">
          <Col md={{ span: 6, offset: 3 }}>
            <Alert
              variant="success"
              onClose={() => setShowAlert(false)}
              dismissible
            >
              Zmiany zostały zapisane!
            </Alert>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default UstawieniaKonta;
