import React, { useContext, useState, useEffect } from "react";
import { UserContext } from "../components/UserContext";
import { Container, Row, Col } from "react-bootstrap";
import UserCard from "../components/UserCard";
import UserContextFilters from "../components/UserContextFilters";

const Gitary = () => {
  const { users, filters } = useContext(UserContext);
  const [gitaryUsers, setGitaryUsers] = useState([]);

  useEffect(() => {
    const filteredUsers = users.filter((user) => {
      let includeUser = true;

      if (filters.miasto && user.miasto !== filters.miasto) {
        includeUser = false;
      }

      if (filters.kategoria && user.kategoria !== filters.kategoria) {
        includeUser = false;
      }

      return includeUser && user.kategoria === "Gitary";
    });

    setGitaryUsers(filteredUsers);
  }, [users, filters]);

  return (
    <UserContextFilters />
