import React, { useState, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "./UserContext";


const LoginForm = () => {
  const { users, setCurrentUser } = useContext(UserContext); // Upewnij się, że setCurrentUser jest dostępne
  const [email, setEmail] = useState("");
  const [haslo, setHaslo] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    const user = users.find((u) => u.email === email && u.haslo === haslo);
    if (user) {
      setCurrentUser(user); // Ustawienie aktualnego użytkownika w kontekście
      navigate("/ustawienia-konta"); // Przekierowanie do strony ustawień konta
    } else {
      setError("Nieprawidłowy e-mail lub hasło");
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <div className="mb-3">
        <span className="input-group-text">
          <i
            className="bx bxs-user-circle fs-2"
            style={{ maxWidth: "30px", marginRight: "10px" }}
          />
          <input
            type="text"
            className="form-control form-control w-100 "
            placeholder="e-mail"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </span>
      </div>
      <div className="mb-3">
        <span className="input-group-text">
          <i
            className="bx bxs-lock-alt fs-2"
            style={{ maxWidth: "30px", marginRight: "10px" }}
          />
          <input
            type="password"
            className="form-control form-control w-100"
            placeholder="hasło"
            value={haslo}
            onChange={(e) => setHaslo(e.target.value)}
          />
        </span>
      </div>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="input-group mb-3 d-flex justify-content-between">
        <div className="form-check">
          <input type="checkbox" className="form-check-input" id="formCheck" />
          <label htmlFor="formCheck" className="form-check-label text-secondary">
            <small>Zapamiętaj mnie</small>
          </label>
        </div>
        <div>
          <small>
            <a href="#" className="text-light">Utwórz nowe hasło</a>
          </small>
        </div>
      </div>
      <div className="loginBtn">
        <button type="submit" className="btn btn-primary w-100" id="saveBtn">Zaloguj</button>
      </div>
      <div className="text-center">
        <small>
          Nie masz konta?{" "}
          <Link className="link" to="/rejestracja"><span style={{color: "#f8b296"}}>
            Zarejestruj się
          </span></Link>
        </small>
      </div>
    </form>
  );
};

export default LoginForm;
