import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import { UserContext } from "./UserContext";

const RegisterForm = () => {
  const { users, setCurrentUser } = useContext(UserContext);
  const navigate = useNavigate();

  // Definicja schematu walidacji z Yup
  const validationSchema = Yup.object().shape({
    email: Yup.string().email("Nieprawidłowy adres e-mail").required("Pole jest wymagane"),
    password: Yup.string().required("Pole jest wymagane"),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref("password"), null], "Hasła muszą być identyczne")
      .required("Pole jest wymagane"),
  });

  const initialValues = {
    email: "",
    password: "",
    confirmPassword: "",
  };

  const handleRegister = (values, { setSubmitting, setFieldError }) => {
    // Sprawdzenie, czy użytkownik już istnieje
    const userExists = users.some((u) => u.email === values.email);
    if (userExists) {
      setFieldError("email", "Użytkownik o podanym adresie e-mail już istnieje");
      setSubmitting(false);
      return;
    }

    // Stworzenie nowego użytkownika (tutaj można by użyć odpowiedniej metody do dodawania użytkownika)
    const newUser = {
      id: users.length + 1, // Przykładowe generowanie ID (w praktyce lepiej użyć UUID lub coś podobnego)
      email: values.email,
      haslo: values.password,
    };

    // Ustawienie nowego użytkownika jako aktualnie zalogowanego
    setCurrentUser(newUser);
    navigate("/ustawienia-konta"); // Przekierowanie do strony ustawień konta po zarejestrowaniu
  };

  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={handleRegister}
    >
      {({ isSubmitting }) => (
        <Form>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              E-mail
            </label>
            <Field
              type="email"
              name="email"
              id="email"
              className="form-control"
              placeholder="E-mail"
            />
            <ErrorMessage name="email" component="div" className="alert alert-danger" />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Hasło
            </label>
            <Field
              type="password"
              name="password"
              id="password"
              className="form-control"
              placeholder="Hasło"
            />
            <ErrorMessage name="password" component="div" className="alert alert-danger" />
          </div>
          <div className="mb-3">
            <label htmlFor="confirmPassword" className="form-label">
              Potwierdź hasło
            </label>
            <Field
              type="password"
              name="confirmPassword"
              id="confirmPassword"
              className="form-control"
              placeholder="Potwierdź hasło"
            />
            <ErrorMessage
              name="confirmPassword"
              component="div"
              className="alert alert-danger"
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary"
            disabled={isSubmitting}
          >
            {isSubmitting ? "Przetwarzanie..." : "Zarejestruj się"}
          </button>          
        </Form>
      )}
    </Formik>
  );
};

export default RegisterForm;
