import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Card, Alert } from 'react-bootstrap';
import './UstawieniaKonta.css';

const UstawieniaKonta = () => {
  const [user, setUser] = useState({
    Nazwa: 'Krzysztof Urbański',
    email: 'krzysztofurban@gmail.com',
    numer: '601-249-606',
    opis: 'Muzyk z wieloletnim doświadczeniem...',
  });

  const [showAlert, setShowAlert] = useState(false);

  const handleSaveChanges = () => {
    // Tutaj możesz dodać logikę zapisu zmian, np. wysłanie ich do serwera

    // Pokazanie alertu po zapisaniu zmian
    setShowAlert(true);

    // Ukrycie alertu po 3 sekundach
    setTimeout(() => {
      setShowAlert(false);
    }, 3000);
  };

  return (
    <Container fluid className='pageBackground'>
      <Row className="mt-5">
        <Col md={{ span: 6, offset: 3 }}>
          <Card>
            <Card.Body>
              <Card.Title >Twoje konto</Card.Title>
              <Form>
                <Form.Group>
                  <Form.Label>Imię i Nazwisko</Form.Label>
                  <Form.Control
                    type="text"
                    name="Nazwa"
                    value={user.Nazwa}
                    onChange={(e) => setUser({ ...user, Nazwa: e.target.value })}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>E-mail</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={user.email}
                    onChange={(e) => setUser({ ...user, email: e.target.value })}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Numer kontaktowy</Form.Label>
                  <Form.Control
                    type="text"
                    name="numer"
                    value={user.numer}
                    onChange={(e) => setUser({ ...user, numer: e.target.value })}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Opis</Form.Label>
                  <Form.Control
                    as="textarea"
                    name="opis"
                    value={user.opis}
                    onChange={(e) => setUser({ ...user, opis: e.target.value })}
                  />
                </Form.Group>
                <Button variant="btn btn-primary w-100" id="saveBtn" onClick={handleSaveChanges}>Zapisz zmiany</Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      {showAlert && (
        <Row className="mt-3">
          <Col md={{ span: 6, offset: 3 }}>
            <Alert variant="success" onClose={() => setShowAlert(false)} dismissible>
              Zmiany zostały zapisane!
            </Alert>
          </Col>
        </Row>
      )}
    </Container>
  );
}

export default UstawieniaKonta;
