import React, { useContext } from "react";
import { UserContext } from "../components/UserContext";
import { UserFiltersContext } from "../components/UserFiltersContext"; // Poprawny import
import { Container, Row, Col } from "react-bootstrap";
import UserCard from "../components/UserCard";

const WszystkieOgloszenia = () => {
  const { users } = useContext(UserContext);
  const { filters } = useContext(UserFiltersContext);

  const filteredUsers = users.filter((user) => {
    let includeUser = true;

    if (filters.miasto && user.miasto !== filters.miasto) {
      includeUser = false;
    }

    if (filters.kategoria && user.kategoria !== filters.kategoria) {
      includeUser = false;
    }

    return includeUser;
  });

  return (
    <Container style={{ background: "#111111", padding: "20px" }}>
      <h2>Wszystkie Og≈Çoszenia</h2>
      <Row>
        {filteredUsers.map((user) => (
          <Col key={user.id} md={4} style={{ marginBottom: "20px" }}>
            <UserCard user={user} />
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default WszystkieOgloszenia;
