    {
      id: 3,
      nazwa: "Marcin Kowalski",
      email: "marcin.kowalski@example.com",
      haslo: "Alamakota3!",
      numer: "987-654-321",
      miasto: "Warszawa",
      kategoria: "Gitary",
      opis: "Cześć!\nJestem Marcinem i szukam ludzi do wspólnego grania na gitarze. Gram od kilku lat i chętnie dołączę do zespołu lub spotkam się z innymi muzykami do jam session. Mój ulubiony gatunek to rock klasyczny. Jeśli masz ochotę na wspólne granie, daj znać!",
      widocznosc: true,
      zdjecie: guitarUser2,
    },
    {
      id: 4,
      nazwa: "Karolina Nowak",
      email: "karolina.nowak@example.com",
      haslo: "aAlamakota4!",
      numer: "123-456-789",
      miasto: "Warszawa",
      kategoria: "Wokal",
      opis: "Witam serdecznie!\nJestem Karoliną i szukam zespołu muzycznego lub pianisty do wspólnego tworzenia muzyki. Mam doświadczenie w śpiewie oraz grze na instrumentach klawiszowych. Jestem otwarta na różne style muzyczne i chętnie współpracuję. Jeśli szukasz wokalistki lub pianisty, to zapraszam do kontaktu!",
      widocznosc: true,
      zdjecie: singerUser2,
    },
    {
      id: 5,
      nazwa: "Adam Nowak",
      email: "adam.nowak@example.com",
      haslo: "Alamakota5!",
      numer: "987-654-321",
      miasto: "Warszawa",
      kategoria: "Instrumenty klawiszowe",
      opis: "Witaj!\nJestem Adamem i pasjonuję się grą na instrumentach klawiszowych. Szukam zespołu lub innych muzyków do wspólnego grania i tworzenia muzyki. Mam doświadczenie w różnych gatunkach muzycznych, ale najbardziej lubię grać muzykę klasyczną i popową. Jeśli potrzebujesz klawiszowca w swoim zespole, napisz do mnie!",
