import React, { useState } from "react";
import { Link } from "react-router-dom";
import Hamburger from "hamburger-react";
import '../components/Menu.css'

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="menuContainer">
      <Hamburger toggled={isOpen} toggle={handleClick} color="#ffffff" />
      {isOpen && (
        <div className="menuDropdown">
          <Link className="link" to="/o-nas" onClick={() => setIsOpen(false)}>
            <div className="menuBtn">O nas</div>
          </Link>
          <Link className="link" to="/kalendarz" onClick={() => setIsOpen(false)}>
            <div className="menuBtn">Kalendarz</div>
          </Link>
          <Link className="link" to="/ustawienia-konta" onClick={() => setIsOpen(false)}>
            <div className="menuBtn">Ustawienia konta</div>
          </Link>
          <Link className="link" to="/pomoc" onClick={() => setIsOpen(false)}>
            <div className="menuBtn">Pomoc</div>
          </Link>
        </div>
      )}
    </div>
  );
};

export default Menu;
