import React, { useState, useContext } from "react";
import {
  Form,
  Button,
  Container,
  Row,
  Col,
  Card,
  Alert,
} from "react-bootstrap";
import { UserContext } from "../components/UserContext";
import { UserForm } from "../components/UserForm";
import "./UstawieniaKonta.css";

const UstawieniaKonta = () => {
  const { user, setUser } = useContext(UserContext);
  const [showAlert, setShowAlert] = useState(false);

  const handleSaveChanges = () => {
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 3000);
  };

  const handleFileChange = (e) => {
    setUser({ ...user, zdjecie: URL.createObjectURL(e.target.files[0]) });
  };

  const handleVisibilityChange = () => {
    setUser({ ...user, widocznosc: !user.widocznosc });
  };

  return (
    <Container fluid className="pageBackground">

      {showAlert && (
        <Row className="mt-3">
          <Col md={{ span: 6, offset: 3 }}>
            <Alert
              variant="success"
              onClose={() => setShowAlert(false)}
              dismissible
            >
              Zmiany zosta≈Çy zapisane!
            </Alert>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default UstawieniaKonta;
