import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';

const UstawieniaKonta = () => {
  const [user, setUser] = useState({
    Nazwa: 'Krzysztof Urbański',
    email: 'krzysztofurban@gmail.com',
    numer: '601-249-606',
    opis: 'Muzyk z wieloletnim doświadczeniem...',
  });

  return (
    <Container>
      <Row className="mt-5">
        <Col md={{ span: 6, offset: 3 }}>
          <Card>
            <Card.Body>
              <Card.Title>Ustawienia konta</Card.Title>
              <Form>
                <Form.Group>
                  <Form.Label>Imię i Nazwisko</Form.Label>
                  <Form.Control
                    type="text"
                    name="Nazwa"
                    value={user.Nazwa}
                    onChange={(e) => setUser({ ...user, Nazwa: e.target.value })}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>E-mail</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={user.email}
                    onChange={(e) => setUser({ ...user, email: e.target.value })}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Numer kontaktowy</Form.Label>
                  <Form.Control
                    type="text"
                    name="numer"
                    value={user.numer}
                    onChange={(e) => setUser({ ...user, numer: e.target.value })}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Opis</Form.Label>
                  <Form.Control
                    as="textarea"
                    name="opis"
                    value={user.opis}
                    onChange={(e) => setUser({ ...user, opis: e.target.value })}
                  />
                </Form.Group>
                <Button variant="primary" onClick={() => console.log('Zapisane dane użytkownika:', user)}>Zapisz zmiany</Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default UstawieniaKonta;
