import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';

const UstawieniaKonta = () => {
  onst [user, setUser] = useState({
    username: 'muzyk123',
    email: 'muzyk@example.com',
    bio: 'Muzyk z wieloletnim doświadczeniem...',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Zapisane dane użytkownika:', user);
  };

  return (
    <Container>
      <Row className="mt-5">
        <Col md={{ span: 6, offset: 3 }}>
          <Card>
            <Card.Body>
              <Card.Title>Ustawienia konta</Card.Title>
              <Form onSubmit={handleSubmit}>
                <Form.Group>
                  <Form.Label>Nazwa użytkownika</Form.Label>
                  <Form.Control
                    type="text"
                    name="username"
                    value={user.username}
                    onChange={handleChange}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={user.email}
                    onChange={handleChange}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Biografia</Form.Label>
                  <Form.Control
                    as="textarea"
                    name="bio"
                    value={user.bio}
                    onChange={handleChange}
                  />
                </Form.Group>
                <Button variant="primary" type="submit">Zapisz zmiany</Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  )
}


export default UstawieniaKonta
