import React, { useState } from "react";
import { Form, Button, Container, Row, Col, Card } from "react-bootstrap"; 

const AuthForm = () => {
  const [showRegister, setShowRegister] = useState(false);

  const handleToggleForm = () => {
    setShowRegister(!showRegister);
  };

  return (
    <Container>
      <Row className="justify-content-md-center"> 
        <Col md="auto"> {/* Automatycznie dostosuje szerokość */}
          <Card>
            <Card.Body>
              {showRegister ? (                
                <Form>
                  <Card.Title>Rejestracja</Card.Title>
                  <Form.Group controlId="formBasicEmail">
                    <Form.Label>Adres e-mail</Form.Label>
                    <Form.Control type="email" placeholder="Wprowadź e-mail" />
                  </Form.Group>

                  <Form.Group controlId="formBasicPassword">
                    <Form.Label>Hasło</Form.Label>
                    <Form.Control type="password" placeholder="Hasło" />
                  </Form.Group>

                  <Form.Group controlId="formBasicPasswordConfirm">
                    <Form.Label>Powtórz hasło</Form.Label>
                    <Form.Control type="password" placeholder="Powtórz hasło" />
                  </Form.Group>
                  <Button variant="primary" type="submit" className="w-100 mt-3">
                    Zarejestruj
                  </Button>
                  <Button variant="secondary" onClick={handleToggleForm} className="w-100 mt-2">
                    Powrót do logowania
                  </Button>
                </Form>
              ) : (
                // Formularz logowania
                <Form>
                  <Card.Title>Logowanie</Card.Title>
                  {/* ... (Twój istniejący kod formularza logowania) */}
                  <div className="loginBtn">
                    <Button variant="primary" type="submit" className="w-100">Zaloguj</Button> 
                  </div>
                  <div className="text-center mt-2">
                    <small>
                      Nie masz konta?{" "}
                      <a href="#" onClick={handleToggleForm} style={{color: "#f8b296"}}>
                        Zarejestruj się
                      </a>
                    </small>
                  </div>
                </Form>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default AuthForm;
